(function() {
  // ===== Scroll to top button =====
  function trackScroll() {
    var scrolled = window.pageYOffset;
    var coords = document.documentElement.clientHeight;
    if (scrolled > 232) {
      goTopBtn.classList.add('scrollTop-show');
    } else {
      goTopBtn.classList.remove('scrollTop-show');
    }
  }
  function backToTop() {
    if (window.pageYOffset > 0) {
      window.scrollBy(0, -50);
      setTimeout(backToTop, 0);
    }
  }
  var goTopBtn = document.querySelector('.scrollTop');
  window.addEventListener('scroll', trackScroll);
  goTopBtn.addEventListener('click', backToTop);
  // ===== Setting the header background & icons for a sidebar menu links =====
  var baseUrl = document.getElementById('base-url').hostname;
  var basePath = document.getElementById('base-url').href;
  var link = document.getElementsByClassName('menu-link');
  var icon = document.getElementsByClassName('menu-icon');
  for (i=0; i < link.length; i++) {
    this.url = link[i].hostname;
    this.path = link[i].href;
    if (this.path == basePath) {
      icon[i].setAttribute('style', 'background-image: url(//lh3.googleusercontent.com/-pov6ygFeG48/W3SVar1mLMI/AAAAAAAAMQ8/nLSj_vQxc8YqrB8c3YBSoMWEIL8rSipTgCEwYBhgL/home.png);');
    } else if (this.url == baseUrl) {
      icon[i].setAttribute('style', 'background-image: url(//lh3.googleusercontent.com/-lA9ECEMzduU/W3R_1j_L2WI/AAAAAAAAMQk/sXAc2lMu_JQS2-LZLTfK1ybx5I3_u7E_QCEwYBhgL/article.png);');
    } else {
      icon[i].setAttribute('style', 'background-image: url(//lh3.googleusercontent.com/-7P41kBeLZ7s/W3STuFF3fVI/AAAAAAAAMQw/cuefYOP5OwEm07xY_6P8f3wV1sXTvX2OQCEwYBhgL/open-in-new.png);');
      link[i].setAttribute('target', '_blank');
      link[i].setAttribute('rel', 'noopener nofollow');
    }
  }
  var menuTitle = document.getElementsByClassName('menu-title');
  if (menuTitle != 'underfined') {
    blog_logo = blog_logo.replace('http://','https://');
    menuTitle[0].setAttribute('style', 'background: transparent url(' + blog_logo + ') no-repeat scroll 16px;');
  }
})();
