(function() {
  // ===== Open sidebar menu by clicking on black button =====
  var blackMenuBtn = document.getElementsByClassName('main-menu_button')[0];
  function menuOpen() {
    var sliderClose = document.getElementById('slider').classList.toggle('closed');
    var backgroundToggle = document.getElementById('dark00').classList.toggle('dark0');
    var bodyOverflow = document.getElementById('page').classList.toggle('no-overflow');
  }
  blackMenuBtn.addEventListener('click', menuOpen, false);
  // ===== Open search input by clicking on black button =====
  var blackSearchBtn = document.getElementsByClassName('main-search_button')[0];
  function searchOpen() {
    var searchOpen = document.getElementById('s').classList.toggle('active');
    var backgroundToggle = document.getElementById('dark01').classList.toggle('dark1');
    var bodyOverflow = document.getElementById('page').classList.toggle('no-overflow');
    var focus = document.getElementById('s').focus();
  }
  blackSearchBtn.addEventListener('click', searchOpen, false);
  // ===== Fixed horizontal navigation on index pages =====
  function stickyNav() {
    var navFix = document.querySelector('#main-menu');
    var searchFix = document.querySelector('.main-search_button');
    var menuFix = document.querySelector('.main-menu_button');
    if (window.scrollY >= 232) {
      navFix.classList.add('nav-pin');
      menuFix.classList.add('menuFix');
      searchFix.classList.add('menuFix');
    } else if (window.scrollY < 232) {
      navFix.classList.remove('nav-pin');
      menuFix.classList.remove('menuFix');
      searchFix.classList.remove('menuFix');
    }
  }
  window.addEventListener('scroll', stickyNav);
})();
