(function() {
  // ===== Post reading time counter =====
  var post = document.getElementsByClassName('post-body')[0].innerText;
  var words = (post.length / 200) / 6;
  var time = Math.round(words);
  var out = document.getElementsByClassName('reading-time')[0];
  out.innerHTML = time + ' min read';
  // ===== Fix share buttons =====
  var shareFix = document.querySelector('.post-social-links');
  function stickyShare() {
    if (window.scrollY >= 232) {
      shareFix.classList.add('share-pin');
    } else if (window.scrollY < 232) {
      shareFix.classList.remove('share-pin');
    }
  }
  window.addEventListener('scroll', stickyShare);
  // ===== Setting min-height of post sidebar for main post block =====
  var nav = document.getElementById('post-sidebar'),
      style = window.getComputedStyle(nav),
      height = style.getPropertyValue('height');
  var main = document.getElementById('main');
  main.style.minHeight = 'calc(' + height + ')';
  window.onload = function() {
    // ===== Default avatar for comment authors =====
    var userAvatar = document.querySelectorAll('.avatar-image-container img');
    for (i = 0; i < userAvatar.length; i++) {
      var userUrl = userAvatar[i].getAttribute('src');
      if (userUrl.includes("blogblog.com")) {
        userUrl = userAvatar[i].setAttribute('src', '//www.shareicon.net/data/512x512/2017/06/21/887437_media_512x512.png');
      } else if (userUrl.includes("googleusercontent.com")) {
        var userSize = userUrl.replace("s35-c", "s64-c");
        userUrl = userAvatar[i].setAttribute('src', userSize);
      }
    }
    var commentTime = document.querySelectorAll('.datetime a');
    for (t = 0; t < commentTime.length; t++) {
      commentTime[t].classList.add('darker');
    }
    // ===== Change position of comment actions buttons =====
    var authorName = document.querySelectorAll('cite.user');
    var actions = document.querySelectorAll('.comment-actions');
    for (l = 0; l < authorName.length; ++l) {
      var w = authorName[l].offsetWidth;
      actions[l].setAttribute('style', 'left: calc(80px + ' + w + 'px);');
    }
    var replyElem = document.querySelectorAll('[o="r"]');
    for (r = 0; r < replyElem.length; ++r) {
      replyElem[r].insertAdjacentHTML('afterbegin', '<i class="icon ion-md-undo"/> ');
      replyElem[r].classList.add('darker');
    }
    var deleteElem = document.querySelectorAll('[o="d"]');
    for (d = 0; d < deleteElem.length; ++d) {
      deleteElem[d].insertAdjacentHTML('afterbegin', '<i class="icon ion-md-trash"/> ');
      deleteElem[d].classList.add('darker');
    }
    var commentTime = document.querySelectorAll('.datetime a');
    for (ct = 0; ct < commentTime.length; ct++) {
      var commentId = document.querySelectorAll('.comment-block')[ct].id;
      var currentUrl = commentTime[ct].getAttribute('href');
      var newUrl = commentTime[ct].setAttribute('href', pageUrl + '#c' + commentId);
    }
  }
})();
